import React, { useState } from "react";
import { runPerformanceTest, runFunctionalityTest, runSecurityTest } from "../api";

const TestForm = () => {
  const [url, setUrl] = useState("");
  const [component, setComponent] = useState("");
  const [endpoint, setEndpoint] = useState("");
  const [message, setMessage] = useState("");

  const handlePerformanceTest = async () => {
    try {
      const response = await runPerformanceTest(url);
      setMessage(response.data.message);
    } catch (error) {
      setMessage("Error running performance test");
    }
  };

  const handleFunctionalityTest = async () => {
    try {
      const response = await runFunctionalityTest(component);
      setMessage(response.data.message);
    } catch (error) {
      setMessage("Error running functionality test");
    }
  };

  const handleSecurityTest = async () => {
    try {
      const response = await runSecurityTest(endpoint);
      setMessage(response.data.message);
    } catch (error) {
      setMessage("Error running security test");
    }
  };

  return (
    <div>
      <h2>AI Testing Platform</h2>

      <div>
        <h3>Performance Test</h3>
        <input
          type="text"
          placeholder="Enter URL"
          value={url}
          onChange={(e) => setUrl(e.target.value)}
        />
        <button onClick={handlePerformanceTest}>Run Test</button>
      </div>

      <div>
        <h3>Functionality Test</h3>
        <input
          type="text"
          placeholder="Component (e.g., login)"
          value={component}
          onChange={(e) => setComponent(e.target.value)}
        />
        <button onClick={handleFunctionalityTest}>Run Test</button>
      </div>

      <div>
        <h3>Security Test</h3>
        <input
          type="text"
          placeholder="API Endpoint (e.g., /api/login)"
          value={endpoint}
          onChange={(e) => setEndpoint(e.target.value)}
        />
        <button onClick={handleSecurityTest}>Run Test</button>
      </div>

      <p>{message}</p>
    </div>
  );
};

export default TestForm;
